const Client = require('knexNode/lib/client');
const QueryBuilder = require('knexNode/lib/query/querybuilder');
const QueryInterface = require('knexNode/lib/query/method-constants');

const makeKnex = require('knexNode/lib/knex-builder/make-knex');
const { KnexTimeoutError } = require('knexNode/lib/util/timeout');
const { resolveConfig } = require('knexNode/lib/knex-builder/internal/config-resolver');

function knex(config) {
  const { resolvedConfig, Dialect } = resolveConfig(...arguments);

  const newKnex = makeKnex(new Dialect(resolvedConfig));
  if (resolvedConfig.userParams) {
    newKnex.userParams = resolvedConfig.userParams;
  }
  return newKnex;
}

// Expose Client on the main Knex namespace.
knex.Client = Client;

knex.KnexTimeoutError = KnexTimeoutError;

knex.QueryBuilder = {
  extend: function (methodName, fn) {
    QueryBuilder.extend(methodName, fn);
    QueryInterface.push(methodName);
  },
};

module.exports = knex;
